import React from 'react';
import styled from 'styled-components';

const StatsContainer = styled.div`
  text-align: center;
  background-color: #f0f0f0;
  border: 1px solid #ccc;
  border-radius: 8px;
  padding: 20px;
  margin: 20px;
`;

const Title = styled.h2`
  color: #333;
`;

const StatItem = styled.p`
  margin: 10px 0;
  font-size: 18px;
`;

const CybersecurityStats = ({ attacksDetained, urlsBlocked, objectsNeutralized }) => {
  return (
    <StatsContainer>
      <Title>Líder mundial en ciberseguridad</Title>
      <div>
        <StatItem><strong>{attacksDetained}</strong> de ciberataques detenidos</StatItem>
        <StatItem><strong>{urlsBlocked}</strong> de URL maliciosas únicas bloqueadas</StatItem>
        <StatItem><strong>{objectsNeutralized}</strong> de objetos maliciosos únicos neutralizados</StatItem>
      </div>
    </StatsContainer>
  );
};

export default CybersecurityStats;
