import React, { useEffect, useState } from 'react';
import '../css/CybersecurityStats.css';

const CybersecurityStats = ({ attacksDetained, urlsBlocked, objectsNeutralized }) => {
  const [attacksPercent, setAttacksPercent] = useState(0);
  const [urlsPercent, setUrlsPercent] = useState(0);
  const [objectsPercent, setObjectsPercent] = useState(0);

  useEffect(() => {
    // Función para animar los porcentajes de manera progresiva
    const animatePercentages = () => {
      const animateValue = (statValue, setStatPercent) => {
        let currentPercent = 0;
        const interval = setInterval(() => {
          currentPercent += 1;
          setStatPercent(currentPercent);
          if (currentPercent >= 94) {
            clearInterval(interval);
          }
        }, 20); // Ajusta este valor para cambiar la velocidad de la animación
      };

      // Animar cada porcentaje
      animateValue(attacksPercent, setAttacksPercent);
      animateValue(urlsPercent, setUrlsPercent);
      animateValue(objectsPercent, setObjectsPercent);
    };

    animatePercentages();
  }, [attacksDetained, urlsBlocked, objectsNeutralized]);

  return (
    <section className="cybersecurity-stats-container">
      <h2 className="cybersecurity-stats-heading">Líder mundial en ciberseguridad</h2>
      <div className="cybersecurity-stats-content">
        <div className="stat-item">
          <p className="stat-label">Ciberataques detenidos</p>
          <p className="stat-value">
            <strong>{attacksPercent}%</strong> ({attacksDetained})
          </p>
        </div>
        <div className="stat-item">
          <p className="stat-label">URL maliciosas bloqueadas</p>
          <p className="stat-value">
            <strong>{urlsPercent}%</strong> ({urlsBlocked})
          </p>
        </div>
        <div className="stat-item">
          <p className="stat-label">Objetos maliciosos neutralizados</p>
          <p className="stat-value">
            <strong>{objectsPercent}%</strong> ({objectsNeutralized})
          </p>
        </div>
      </div>
    </section>
  );
};

export default CybersecurityStats;
