import React from "react";
import { signOut } from "firebase/auth";
import { auth } from "./Firebase";
import { useNavigate, Route, Routes } from "react-router-dom";
import Home from "./page/Home";
import Services from "./page/Services";
// Import the SignIn component from the appropriate file
import SignIn from "./page/SignIn";

const LogoutButton = ({ onClick }) => {
  return <button onClick={onClick}>Logout</button>;
};

const HomePage = ({ user }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        // Sign-out successful.
        navigate("/");
        console.log("Signed out successfully");
      })
      .catch((error) => {
        // An error happened.
        console.error("Error signing out:", error);
      });
  };

  return (
    <nav className="home">
      {/* Contenido de la página principal */}
      <h1 className="mision">Tu tranquilidad es nuestra misión</h1>
      <h1 className="vision">
        Tu confianza nos impulsa. Brindamos soluciones de ciberseguridad de
        clase mundial para que puedas enfocarte en hacer crecer tu negocio sin
        temor a interrupciones.
      </h1>
      {user ? <LogoutButton onClick={handleLogout} /> : null}

      {/* Aquí se definen las rutas */}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/services" element={<Services />} />
        <Route path="/signin" element={<SignIn />} />
      </Routes>
    </nav>
  );
};

export default HomePage;
