import React from "react";
import { signOut } from "firebase/auth";
import { auth } from "./Firebase";
import { useNavigate, Redirect } from "react-router-dom";
import Services from "./page/Services"; // Import the Services component

const Home = ({ user }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        // Sign-out successful.
        navigate("/");
        console.log("Signed out successfully");
      })
      .catch((error) => {
        // An error happened.
        console.error("Error signing out:", error);
      });
  };

  return (
    <div className="home">
      <h1 className="mision">Tu tranquilidad es nuestra misi√≥n</h1>
      <h1 className="vision">
        Tu confianza nos impulsa. Brindamos soluciones de ciberseguridad de
        clase mundial para que puedas enfocarte en hacer crecer tu negocio sin
        temor a interrupciones.
      </h1>
      <button className="buttonn" onClick={handleLogout}>
        Analisis Gratuito
      </button>
      {user ? (
        <>
          <button onClick={handleLogout}>Logout</button>
          {/* Redirect to Services if user is logged in */}
          <Redirect to="/services" />
        </>
      ) : null}
    </div>
  );
};

export default Home;
