import React from "react";
import { signOut } from "firebase/auth";
import { auth } from "./Firebase";
import { useNavigate } from "react-router-dom";

const Home = ({ user }) => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await signOut(auth);
      // Sign-out successful.
      navigate("/");
      console.log("Signed out successfully");
    } catch (error) {
      // An error happened.
      console.error("Error signing out:", error);
    }
  };

  return (
    <div className="home">
      <div className="content">
        <h1 className="mision">Tu tranquilidad es nuestra misi√≥n</h1>
        <h1 className="vision">
          Tu confianza nos impulsa. Brindamos soluciones de ciberseguridad de
          clase mundial para que puedas enfocarte en hacer crecer tu negocio sin
          temor a interrupciones.
        </h1>
        {user ? (
          <>
            <button className="buttonn" onClick={handleLogout}>
              Analisis Gratuito
            </button>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <p>Logged out</p>
        )}
      </div>
      {/* Additional content or components for logged-in users */}
      {user && (
        <div className="additional-content">
          <h2>Welcome, {user.email}!</h2>
          {/* Add more content for logged-in users here */}
        </div>
      )}
    </div>
  );
};

export default Home;
