import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import "../css/Navbar.css";

const Navbar = () => {
  const [isScrollLocked, setScrollLocked] = useState(false);

  const navLinks = [
    { path: "/", text: "Inicio" },
    { path: "/about", text: "Conócenos" },
    { path: "/services", text: "Servicios" },
    { path: "/contact", text: "Contacto" },
  ];

  const handleClick = () => {
    // Cambiar el estado de isScrollLocked cuando se hace clic en un enlace del navbar
    setScrollLocked(!isScrollLocked);
  };

  return (
    <nav className={`navbar ${isScrollLocked ? "scroll-locked" : ""}`}>
      <div className="navbar-container">
        <NavLink exact to="/" className="logo-link">
          Your Logo
        </NavLink>

        <ul className="nav-links">
          {navLinks.map((link, index) => (
            <li key={index}>
              <NavLink
                to={link.path}
                activeClassName="active-link"
                onClick={handleClick}
              >
                {link.text}
              </NavLink>
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
