import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./page/Firebase";
import Signup from "./page/Signup";
import Login from "./page/Login";
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import ContactForm from "./page/ContactForm";
import PaginaPrincipal from "./page/PaginaPrincipal";
import About from "./page/About";
import CybersecurityStats from "./page/CybersecurityStats";

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        // User is signed in
        setUser(user);
      } else {
        // User is signed out
        setUser(null);
      }
    });

    // Clean up the listener on unmount
    return () => unsubscribe();
  }, []);

  return (
    <Router>
      <Navbar user={user} />
      <PaginaPrincipal user={user} />
      <br />
      <CybersecurityStats user={user} />
      <br />
      <Services user={user} />
      <br />
      <About user={user} />
      <br />
      <ContactForm user={user} />
      <br />



      {/* Define your routes inside the <Routes> element */}
      <Routes>
        <Route path="/signup" element={<Signup />} />
        <Route path="/login" element={<Login />} />
        <Route path="/services" element={<Services />} />
        <Route path="/contact" element={<ContactForm/>} />  
        <Route path="/about" element={<About />} />
        <Route path="/cybersecurity" element={<CybersecurityStats />} />
      </Routes>
    </Router>
  );
}

export default App;
