import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import { debounce } from "lodash";
import { useLocation } from "react-router-dom"; // Importa useLocation
import "../css/Navbar.css";

const Navbar = () => {
  const [isScrollLocked, setScrollLocked] = useState(false);
  const location = useLocation(); // Usa el hook useLocation

  const navLinks = [
    { path: "/", text: "Inicio" },
    { path: "/services", text: "Servicios" },
    { path: "/partners", text: "Proyectos" },
    { path: "/cybersecurity", text: "Métricas" },
    { path: "/about", text: "Conócenos" },
    { path: "/contact", text: "Contacto" },
  ];

  const handleClick = debounce(() => {
    if (!isScrollLocked) {
      // Debounce the scroll locking for 800ms
      setScrollLocked(true);
      setTimeout(() => {
        setScrollLocked(false);
      }, 800);
    }
  }, 800);

  const renderNavLinks = () => {
    return navLinks.map(({ path, text }) => (
      <li key={path}>
        {/* Usa la función isActive para aplicar el estilo al link activo */}
        <NavLink
          to={path}
          activeClassName="active-link"
          onClick={handleClick}
          isActive={(match, location) => {
            // Verifica si la ruta actual coincide con la ruta del link
            return match || location.pathname === path;
          }}
        >
          {text}
        </NavLink>
      </li>
    ));
  };

  return (
    <nav className={`navbar ${isScrollLocked ? "scroll-locked" : ""}`}>
      <div className="navbar-container">
        <NavLink exact to="/" className="logo-link">
          Your Logo
        </NavLink>

        <ul className="nav-links">{renderNavLinks()}</ul>
      </div>
    </nav>
  );
};

export default Navbar;
