import React from 'react';
import '../css/Footer.css'; // Importa el archivo CSS

const Footer = ({ companyName, logoUrl }) => {
  const contactInformation = [
    'Dirección de la empresa',
    'Teléfono: 123-456-7890',
    'Email: info@empresa.com',
  ];

  return (
    <footer className="footer">
      <div className="container">
        <div className="logo">
          {/* Display the company logo if logoUrl is provided */}
          {logoUrl && <img src={logoUrl} alt={companyName} />}
        </div>
        <div className="info">
          {/* Display contact information using a map */}
          {contactInformation.map((info, index) => (
            <p key={index}>{info}</p>
          ))}
        </div>
      </div>
      <div className="legal">
        <div className="privacy">
          {/* Open the privacy policy link in a new tab */}
          <a href="/politica-de-privacidad" target="_blank" rel="noopener noreferrer">
            Política de privacidad
          </a>
        </div>
        <div className="cookies">
          {/* Open the cookies policy link in a new tab */}
          <a href="/politica-de-cookies" target="_blank" rel="noopener noreferrer">
            Política de cookies
          </a>
        </div>
      </div>
      <div className="copyright">
        <p>© {new Date().getFullYear()} {companyName}. Todos los derechos reservados.</p>
      </div>
    </footer>
  );
};

export default Footer;
