import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Home from "./page/Home";
import Signup from "./page/Signup";
import Login from "./page/Login";
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./page/Firebase";

const App = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    onAuthStateChanged(auth, function (user) {
      setUser(user);
    });
  }, []);

  return (
    <Router>
      <div>
        {/* Render the Navbar outside the Routes */}
        <Navbar user={user} />

        <section>
          <Routes>
            {/* If the user is not logged in, show the Home page */}
            {!user && <Route path="/" element={<Home user={user} />} />}
            {/* If the user is logged in, show the Services page */}
            {user && <Route path="/" element={<Services />} />}
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </section>
      </div>
    </Router>
  );
};

export default App;
