import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import '../css/CybersecurityStats.css';

const CybersecurityStats = ({ attacksDetained, urlsBlocked, objectsNeutralized }) => {
  const [attacksPercent, setAttacksPercent] = useState(0);
  const [urlsPercent, setUrlsPercent] = useState(0);
  const [objectsPercent, setObjectsPercent] = useState(0);

  useEffect(() => {
    const animatePercentages = (value, setStatPercent, targetPercent) => {
      const interval = setInterval(() => {
        setStatPercent((prevPercent) => {
          const nextPercent = prevPercent + 1;
          return Math.min(nextPercent, targetPercent);
        });
      }, 10); // Adjust the interval time for smoother animation
      return () => clearInterval(interval);
    };

    animatePercentages(attacksDetained, setAttacksPercent, 93);
    animatePercentages(urlsBlocked, setUrlsPercent, 96);
    animatePercentages(objectsNeutralized, setObjectsPercent, 94);
  }, [attacksDetained, urlsBlocked, objectsNeutralized]);

  const formatNumber = (number) => {
    return number.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ',');
  };

  return (
    <section className="cybersecurity-stats-container">
      <h2 className="cybersecurity-stats-heading">LÃ­der mundial en ciberseguridad</h2>
      <div className="cybersecurity-stats-content">
        <div className="stat-item">
          <p className="stat-label">Ciberataques detenidos</p>
          <p className="stat-value">
            <strong>{attacksPercent}%</strong> ({formatNumber(attacksDetained)})
          </p>
        </div>
        <div className="stat-item">
          <p className="stat-label">URL maliciosas bloqueadas</p>
          <p className="stat-value">
            <strong>{urlsPercent}%</strong> ({formatNumber(urlsBlocked)})
          </p>
        </div>
        <div className="stat-item">
          <p className="stat-label">Objetos maliciosos neutralizados</p>
          <p className="stat-value">
            <strong>{objectsPercent}%</strong> ({formatNumber(objectsNeutralized)})
          </p>
        </div>
      </div>
    </section>
  );
};

CybersecurityStats.propTypes = {
  attacksDetained: PropTypes.number.isRequired,
  urlsBlocked: PropTypes.number.isRequired,
  objectsNeutralized: PropTypes.number.isRequired,
};

export default CybersecurityStats;
