import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import ContactForm from "./page/ContactForm";
import PaginaPrincipal from "./page/PaginaPrincipal";
import About from "./page/About";
import CybersecurityStats from "./page/CybersecurityStats";
import firestore from "./page/FirebaseConfig";
import Partners from "./page/Partners";
import Footer from "./page/Footer";
import PrivacyPolicy from "./page/PoliticaPrivacidad";
import TermsAndConditions from "./page/TermsAndConditions";
import CookiesPolicy from "./page/CookiesPolicy";
import NotFoundComponent from "./page/NotFound";

function App() {
  const [dataFromServer, setDataFromServer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://217.160.0.78:3080")
      .then(response => response.text())
      .then(data => {
        setDataFromServer(data);
        // Redirect the user to the IP address when data is received
        navigate("http://217.160.0.78:3080");
      })
      .catch(error => {
        console.error("Error al obtener los datos del servidor:", error);
      });
  }, [navigate]);

  return (
    <Router>
      <Navbar />
      <div>
        {dataFromServer ? (
          <p>Respuesta del servidor: {dataFromServer}</p>
        ) : (
          <p>Cargando...</p>
        )}
      </div>
      <Routes>
        {/* ... Rest of the routes ... */}
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
