// Navbar.js
import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import { UserContext } from "../UserContext";
import { auth, provider } from "./Firebase"; // Make sure to import your Firebase auth instance
import "../css/Navbar.css";

const Navbar = () => {
  const { user, handleLogout } = useContext(UserContext);

  const handleLogoutClick = () => {
    // Call the handleLogout function to clear the user state
    handleLogout();
  };

  const onGoogleLogin = () => {
    // Implement the Google login logic using Firebase's signInWithPopup method
    signInWithPopup(auth, provider)
      .then((result) => {
        const user = result.user;
        handleLogin(user); // Update the user state using handleLogin
      })
      .catch((error) => {
        // Handle Errors here.
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
      });
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <NavLink exact to="/" className="logo">
          Your Logo
        </NavLink>

        <div className="nav-links">
          <NavLink exact to="/" activeClassName="active-link">
            Inicio
          </NavLink>
          <NavLink to="/about" activeClassName="active-link">
            Conocenos
          </NavLink>
          <NavLink to="/services" activeClassName="active-link">
            Servicios
          </NavLink>
          <NavLink to="/contact" activeClassName="active-link">
            Contacto
          </NavLink>
          {user ? (
            <>
              <span className="user-name">Hola, {user.displayName}</span>
              <button onClick={handleLogoutClick}>Logout</button>
            </>
          ) : (
            <button onClick={onGoogleLogin}>Login with Google</button>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
