import React, { useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate, Switch, useLocation } from 'react-router-dom';
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import ContactForm from "./page/ContactForm";
import PaginaPrincipal from "./page/PaginaPrincipal";
import About from "./page/About";
import CybersecurityStats from "./page/CybersecurityStats";
import firestore from "./page/FirebaseConfig";
import Partners from "./page/Partners";
import Footer from "./page/Footer";
import PrivacyPolicy from "./page/PoliticaPrivacidad";
import TermsAndConditions from "./page/TermsAndConditions";
import CookiesPolicy from "./page/CookiesPolicy";
import NotFoundComponent from "./page/NotFound";

function App() {
  // Obtener la ubicación actual utilizando el gancho useLocation
  const location = useLocation();

  // Verificar si la aplicación está en la página de inicio ("/")
  const isHomePage = location.pathname === "/";

  useEffect(() => {
    // Redirigir al usuario al dominio "www.innovex.es" si está en la página de inicio ("/")
    if (isHomePage) {
      window.location.href = "http://www.innovex.es";
    }
  }, [isHomePage]);

  return (
    <Router>
      <Navbar />
      {/* Puedes eliminar esta sección si no deseas mostrar la respuesta del servidor */}
      <div>
        <p>Cargando...</p>
      </div>
      <Routes>
        <Route path="/" element={<PaginaPrincipal />} />
        <Route path="/about" element={<About />} />
        <Route path="/services" element={<Services />} />
        {firestore && (
          <Route path="/contact" element={<ContactForm firestore={firestore} />} />
        )}
        <Route path="/cybersecurity" element={<CybersecurityStats />} />
        <Route path="/partners" element={<Partners />} />
        <Route path="/privacy" element={<PrivacyPolicy />} />
        <Route path="/terms" element={<TermsAndConditions />} />
        <Route path="/cookies" element={<CookiesPolicy />} />
        {/* Ruta de respaldo para manejar el error 404 */}
        <Route path="*" element={<NotFoundComponent />} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
