import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import '../css/CybersecurityStats.css';

const CybersecurityStats = ({ label, value }) => {
  const [percentage, setPercentage] = useState(0);

  useEffect(() => {
    const targetPercentage = Math.min(Math.round((value / 1000) * 100), 100); // Assuming value is out of 1000
    const interval = setInterval(() => {
      setPercentage((prevPercentage) => Math.min(prevPercentage + 1, targetPercentage));
    }, 10); // Adjust the interval time for smoother animation

    return () => clearInterval(interval);
  }, [value]);

  const formatNumber = (number) => {
    return number.toLocaleString();
  };

  return (
    <div className="cybersecurity-stat">
      <p className="stat-label">{label}</p>
      <p className="stat-value">
        <strong>{percentage}%</strong> ({formatNumber(value)})
      </p>
    </div>
  );
};

CybersecurityStats.propTypes = {
  label: PropTypes.string.isRequired,
  value: PropTypes.number.isRequired,
};

const CybersecurityStatsContainer = () => {
  return (
    <section className="cybersecurity-stats-container">
      <h2 className="cybersecurity-stats-heading">LÃ­der mundial en ciberseguridad</h2>
      <div className="cybersecurity-stats-content">
        <CybersecurityStats label="Ciberataques detenidos" value={93} />
        <CybersecurityStats label="URL maliciosas bloqueadas" value={96} />
        <CybersecurityStats label="Objetos maliciosos neutralizados" value={94} />
      </div>
    </section>
  );
};

export default CybersecurityStatsContainer;
