import React from "react";
import "../css/Services.css";

const ServicePackage = ({ title, description, features, isMostPopular }) => {
  return (
    <div className={`service-package ${isMostPopular ? "most-popular" : ""}`}>
      <h2>{title}</h2>
      <p>{description}</p>
      <ul>
        {features.map((feature, index) => (
          <li key={index}>{feature}</li>
        ))}
      </ul>
      <button className="btn">Solicitar Información</button>
      {isMostPopular && <div className="most-popular-badge">Más Vendido</div>}
    </div>
  );
};

const Services = () => {
  return (
    <section className="services">
      <ServicePackage
        isMostPopular={true}
        title="Paquete de Prueba de Evaluación y Penetración"
        description="Nuestro paquete de Prueba de Evaluación y Penetración ofrece una evaluación exhaustiva de tus sistemas y redes para identificar posibles vulnerabilidades y brechas de seguridad. Nuestros expertos altamente capacitados simularán ataques para poner a prueba tus defensas cibernéticas y proporcionarán un informe detallado con recomendaciones para fortalecer tu seguridad."
        features={[
          "Incluir identificación de activos",
          "Incluir escaneo automático",
          "Incluir escaneo manual avanzado",
          "Volver a escanear la evaluación",
        ]}
      />

      <ServicePackage
      title="Penetración para aplicaciones web y móviles."
      description="Nuestro servicio incluye una revisión exhaustiva de tu aplicación web o móvil, seguido de una nueva prueba después de realizar las correcciones necesarias. Además, recibirás un informe detallado y actualizado sobre las vulnerabilidades detectadas y las medidas implementadas. Todo esto dentro de un límite de 14 horas de trabajo manual. Nuestro enfoque eficiente y preciso te brindará tranquilidad y confianza en la seguridad de tu sistema, protegiéndote contra posibles amenazas y manteniendo tus datos seguros."
      features={[
        "Incluir identificación de activos",
        "Incluir escaneo automático",
        "Incluir escaneo manual avanzado",
        "Volver a escanear la evaluación",

        ]}
      />

<ServicePackage
      title="Auditoría iso con inspección de código."
      description="Amplia prueba de Crystal Box de bare metal, topología de red y seguridad de aplicaciones según las normas ISO."
      features={[
        "Incluir identificación de activos",
        "Incluir escaneo automático",
        "Incluir escaneo manual avanzado",
        "Volver a escanear la evaluación",

        ]}
      />
    </section>
  );
};

export default Services;
