import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import ContactForm from "./page/ContactForm";
import PaginaPrincipal from "./page/PaginaPrincipal";
import About from "./page/About";
import CybersecurityStats from "./page/CybersecurityStats";
import { onAuthStateChanged } from "firebase/auth"; // Import onAuthStateChanged from Firebase auth module
import { initializeApp } from "firebase/app"; // Import the initializeApp function
import { getAuth } from "firebase/auth"; // Import the getAuth function to access auth object

// Initialize Firebase app
const firebaseConfig = {
  // Your Firebase configuration
};

const firebaseApp = initializeApp(firebaseConfig);

function App() {
  const [user, setUser] = React.useState(null);

  React.useEffect(() => {
    // Get the auth object using getAuth function
    const auth = getAuth(firebaseApp);

    // Now use the 'auth' object
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        // User is signed in
        setUser(user);
      } else {
        // User is signed out
        setUser(null);
      }
    });

    // Clean up the listener on unmount
    return () => unsubscribe();
  }, []);

  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<PaginaPrincipal user={user} />} />
        <Route path="/about" element={<About user={user} />} />
        <Route path="/services" element={<Services user={user} />} />
        <Route path="/contact" element={<ContactForm user={user} />} />
        <Route path="/cybersecurity" element={<CybersecurityStats user={user} />} />
      </Routes>
    </Router>
  );
}

export default App;
