import React, { useState } from 'react';
import { firestore } from '../page/FirebaseConfig';

const ContactForm = ({ firestore }) => { // Asegúrate de que el nombre de la prop sea correcto (firestore)  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Guardar la información en Firebase
    firestore.collection('contact_messages').add({
      name,
      email,
      message,
      timestamp: firestore.FieldValue.serverTimestamp(),
    });

    // Limpiar el formulario después de enviar
    setName('');
    setEmail('');
    setMessage('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Nombre:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="message">Mensaje:</label>
        <textarea
          id="message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
      </div>
      <button type="submit">Enviar</button>
    </form>
  );
};

export default ContactForm;
