import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/HeroSection.css';

const HeroSection = () => {
  const navigate = useNavigate();

  const handleButtonClick = () => {
    navigate('/contact'); // Navega al formulario de contacto al hacer clic en el bot칩n
  };

  return (
    <section className="hero-section">
      <div className="hero-content">
        <h2 className="hor">
          Tu confianza nuestra fortaleza; tu seguridad, nuestra prioridad.
        </h2>
        <p className="her">
          En un mundo cada vez m치s interconectado, nuestra empresa de ciberseguridad se compromete a proteger tus datos y privacidad, brind치ndote soluciones avanzadas y una defensa integral contra las amenazas.
        </p>
        <button className="cta-button" onClick={handleButtonClick}>
          Prueba de escaneo gratuita
        </button>
      </div>
      <div className="hero-image">
        <img
          className="fotito"
          src="https://i.blogs.es/8122e1/istock-1194430863/1366_2000.jpeg"
          alt="Hero"
        />
      </div>
    </section>
  );
};

export default HeroSection;
