// src/App.js (or index.js)
import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./page/Navbar";
import Services from "./page/Services";
import ContactForm from "./page/ContactForm";
import PaginaPrincipal from "./page/PaginaPrincipal";
import About from "./page/About";
import CybersecurityStats from "./page/CybersecurityStats";
import firebaseConfig from "./page/FirebaseConfig"; // Corrected import path
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";

// Initialize Firebase only once at the root of your application
const app = initializeApp(firebaseConfig);
const firestore = getFirestore(app);

function App() {
  // Pass the 'firestore' object as a prop to components that need it
  return (
    <>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/" element={<PaginaPrincipal />} />
          <Route path="/about" element={<About />} />
          <Route path="/services" element={<Services />} />
          <Route path="/contact" element={<ContactForm firestore={firestore} />} />
          <Route path="/cybersecurity" element={<CybersecurityStats />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
