import React from "react";
import { signOut } from "firebase/auth";
import { auth } from "./Firebase";
import { useNavigate, Route, Routes } from "react-router-dom";
import SignIn from "./pages/SignIn";


const Home = ({ user }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    signOut(auth)
      .then(() => {
        // Sign-out successful.
        navigate("/");
        console.log("Signed out successfully");
      })
      .catch((error) => {
        // An error happened.
        console.error("Error signing out:", error);
      });
  };

  return (
    <nav className="home">
      {/* Contenido de la página principal */}
      <h1 className="mision">Tu tranquilidad es nuestra misión</h1>
      <h1 className="vision">Tu confianza nos impulsa. Brindamos soluciones de ciberseguridad de clase mundial para que puedas enfocarte en hacer crecer tu negocio sin temor a interrupciones.</h1>
      <button className="buttonn" onClick={handleLogout}>Analisis Gratuito</button>
      <div>
        {user ? (
          <button onClick={handleLogout}>Logout</button>
        ) : (
          <p></p>
        )}
      </div>

      {/* Aquí se definen las rutas */}
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/services" element={<Services />} />
      </Routes>
    </nav>
  );
};

export default Home;
