// Navbar.js
import React, { useContext } from "react";
import { NavLink } from "react-router-dom";
import { UserContext } from "../page/UserContext";
import "../css/Navbar.css";

const Navbar = () => {
  const { user, handleLogout } = useContext(UserContext);

  const handleLogoutClick = () => {
    handleLogout(); // Call the handleLogout function to clear the user state
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <NavLink exact to="/" className="logo">
          Your Logo
        </NavLink>

        <div className="nav-links">
          <NavLink exact to="/" activeClassName="active-link">
            Inicio
          </NavLink>
          <NavLink to="/about" activeClassName="active-link">
            Conocenos
          </NavLink>
          <NavLink to="/services" activeClassName="active-link">
            Servicios
          </NavLink>
          <NavLink to="/contact" activeClassName="active-link">
            Contacto
          </NavLink>


          {user ? (
            <>
              <span className="user-name">uiqfnquofoqnfuoq{user.name}</span>
              <button onClick={handleLogoutClick}>Logout</button>
            </>
          ) : (
            <NavLink to="/login" activeClassName="active-link">
              Iniciar Sesion
            </NavLink>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
