import React, { useState, useContext } from "react";
import { signInWithEmailAndPassword, signInWithPopup } from "firebase/auth";
import { auth, provider } from "./Firebase";
import { NavLink, useNavigate } from "react-router-dom";
import { UserContext } from "../UserContext"; // Import the UserContext

const Login = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { handleLogin } = useContext(UserContext); // Access the handleLogin function from the UserContext

  const onLogin = (e) => {
    e.preventDefault();
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredential) => {
        // Signed in
        const user = userCredential.user;
        // Assuming user.displayName contains the user's name after registration or login
        const userData = {
          uid: user.uid,
          email: user.email,
          name: user.displayName, // Store the user's name in the userData object
        };
        handleLogin(userData); // Update the user state using handleLogin
        navigate("/");
      })
      .catch((error) => {
        alert("Invalid email or password");
        const errorCode = error.code;
        const errorMessage = error.message;
        console.log(errorCode, errorMessage);
      });
  };

  return (
    <main className="container">
      <section className="card">
        <div>
          <h1>IBFUIWENFONOEFNWENFNEIOIEJIQJ</h1>


          <form className="form">
            <div className="form-group">
              <label className="label" htmlFor="email-address">
                Email address
              </label>
              <input
                className="input"
                id="email-address"
                name="email"
                type="email"
                required
                placeholder="Email address"
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label className="label" htmlFor="password">
                Password
              </label>
              <input
                className="input"
                id="password"
                name="password"
                type="password"
                required
                placeholder="Password"
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>

            <div>
              <button className="button" onClick={onLogin}>
                Login
              </button>
              <button className="button google-button" onClick={onGoogleLogin}>
                Login with Google
              </button>
            </div>
          </form>

          <p className="text-sm text-white text-center">
            No account yet? <NavLink to="/signup">Sign up</NavLink>
          </p>
        </div>
      </section>
    </main>
  );
};

export default Login;
