import React, { useState } from 'react';
import firebase from './page/firebaseConfig'; // Ruta relativa al archivo actual
import '../css/ContactForm.css';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Enviar los datos a Firebase Realtime Database
    const databaseRef = firebase.database().ref('contactMessages');
    databaseRef.push(formData);

    // Luego de enviar los datos, puedes limpiar el formulario
    setFormData({
      name: '',
      email: '',
      message: '',
    });
  };

  return (
    <div className="contact-form-container">
      {/* ... rest of the form JSX ... */}
    </div>
  );
};

export default ContactForm;
